sect2.example:
    Atribuição de Objetos


<?php
$atribuido  =  $instancia;
$referencia =& $instancia;


$instancia->var = '$atribuido terá esse valor';

$instancia = null; /* $instancia e $referencia se tornam nulos (null) */


var_dump($instancia);
var_dump($referencia);
var_dump($atribuido);
?>


    &example.outputs;


NULL
NULL
object(ClasseSimples)#1 (1) {
   ["var"]=>
     string(26) "$atribuido terá esse valor"
}

    Herança Simples


<?php
class ClasseHerdeira extends ClasseSimples {
  /* Redefine o método pai */
  function mostraVar() {
    echo "Class Herdeira\n";
    parent::mostraVar();
  }
}

$herdeira = new ClasseHerdeira();
$herdeira->mostraVar();
?>


    &example.outputs;


Class Herdeira
um valor default
