sect2.example:
   Criando um Fantasma Lento


<?php
class Example
{
    public function __construct(public int $prop)
    {
        echo __METHOD__, "\n";
    }
}

$reflector = new ReflectionClass(Example::class);
$lazyObject = $reflector->newLazyGhost(function (Example $object) {
    // Inicializa o objeto no local
    $object->__construct(1);
});

var_dump($lazyObject);
var_dump(get_class($lazyObject));

// Dispara a inicialização
var_dump($lazyObject->prop);
?>


   &example.outputs;


lazy ghost object(Example)#3 (0) {
["prop"]=>
uninitialized(int)
}
string(7) "Example"
Example::__construct
int(1)

   Criando um Proxy Lento


<?php
class Example
{
    public function __construct(public int $prop)
    {
        echo __METHOD__, "\n";
    }
}

$reflector = new ReflectionClass(Example::class);
$lazyObject = $reflector->newLazyProxy(function (Example $object) {
    // Cria e retorna a instância real
    return new Example(1);
});

var_dump($lazyObject);
var_dump(get_class($lazyObject));

// Dispara a inicialização
var_dump($lazyObject->prop);
?>


   &example.outputs;


lazy proxy object(Example)#3 (0) {
  ["prop"]=>
  uninitialized(int)
}
string(7) "Example"
Example::__construct
int(1)
