para.example:
   Operações bitwise AND, OR e XOR em inteiros


<?php
/*
 * Ignore essa primeira parte,
 * que é apenas para deixar a impressão mais limpa.
 */

$format = '(%1$2d = %1$04b) = (%2$2d = %2$04b)'
        . ' %3$s (%4$2d = %4$04b)' . "\n";

echo <<<EOH
 ---------     ---------  -- ---------
 result        value      op test
 ---------     ---------  -- ---------
EOH;


/*
 * Agora os exemplos
 */

$values = array(0, 1, 2, 4, 8);
$test = 1 + 4;

echo "\n Operador de bit E \n";
foreach ($values as $value) {
    $result = $value & $test;
    printf($format, $result, $value, '&', $test);
}

echo "\n Operador de bit OU inlusivo \n";
foreach ($values as $value) {
    $result = $value | $test;
    printf($format, $result, $value, '|', $test);
}

echo "\n Operador de bit OU Exclusivo (XOR) \n";
foreach ($values as $value) {
    $result = $value ^ $test;
    printf($format, $result, $value, '^', $test);
}
?>


   &example.outputs;


 ---------     ---------  -- ---------
 result        value      op test
 ---------     ---------  -- ---------
 Operador de bit E
( 0 = 0000) = ( 0 = 0000) & ( 5 = 0101)
( 1 = 0001) = ( 1 = 0001) & ( 5 = 0101)
( 0 = 0000) = ( 2 = 0010) & ( 5 = 0101)
( 4 = 0100) = ( 4 = 0100) & ( 5 = 0101)
( 0 = 0000) = ( 8 = 1000) & ( 5 = 0101)

 Operador de bit OU inlusivo
( 5 = 0101) = ( 0 = 0000) | ( 5 = 0101)
( 5 = 0101) = ( 1 = 0001) | ( 5 = 0101)
( 7 = 0111) = ( 2 = 0010) | ( 5 = 0101)
( 5 = 0101) = ( 4 = 0100) | ( 5 = 0101)
(13 = 1101) = ( 8 = 1000) | ( 5 = 0101)

 Operador de bit OU Exclusivo (XOR)
( 5 = 0101) = ( 0 = 0000) ^ ( 5 = 0101)
( 4 = 0100) = ( 1 = 0001) ^ ( 5 = 0101)
( 7 = 0111) = ( 2 = 0010) ^ ( 5 = 0101)
( 1 = 0001) = ( 4 = 0100) ^ ( 5 = 0101)
(13 = 1101) = ( 8 = 1000) ^ ( 5 = 0101)

Deslocamento de bits em inteiros
<?php /* * Aqui estão os exemplos. */
echo "\n--- MOVENDO BITS A DIREITA EM INTEIROS POSITIVOS ---\n";
$val = 4; $places = 1; $res = $val >> $places; p($res, $val, '>>', $places, 'cópia do bit de sinal trocada para a esquerda');
$val = 4; $places = 2; $res = $val >> $places; p($res, $val, '>>', $places);
$val = 4; $places = 3; $res = $val >> $places; p($res, $val, '>>', $places, 'move os bits para fora da direita');
$val = 4; $places = 4; $res = $val >> $places; p($res, $val, '>>', $places, 'mesmo resultado que acima; não se pode mover além do 0');
echo "\n--- MOVENDO BITS A DIREITA EM INTEIROS NEGATIVOS ---\n";
$val = -4; $places = 1; $res = $val >> $places; p($res, $val, '>>', $places, 'cópia do bit de sinal trocada para a esquerda');
$val = -4; $places = 2; $res = $val >> $places; p($res, $val, '>>', $places, 'move os bits para fora da direita');
$val = -4; $places = 3; $res = $val >> $places; p($res, $val, '>>', $places, 'mesmo resultado que acima; não se pode mover além do -1');
echo "\n--- MOVENDO BITS A ESQUERDA EM INTEIROS POSITIVOS ---\n";
$val = 4; $places = 1; $res = $val << $places; p($res, $val, '<<', $places, 'preenche com zeros o lado direito');
$val = 4; $places = (PHP_INT_SIZE * 8) - 4; $res = $val << $places; p($res, $val, '<<', $places);
$val = 4; $places = (PHP_INT_SIZE * 8) - 3; $res = $val << $places; p($res, $val, '<<', $places, 'bit de sinal movido para fora');
$val = 4; $places = (PHP_INT_SIZE * 8) - 2; $res = $val << $places; p($res, $val, '<<', $places, 'bits movidos para fora da esquerda');
echo "\n--- MOVENDO BITS A ESQUERDA EM INTEIROS NEGATIVOS ---\n";
$val = -4; $places = 1; $res = $val << $places; p($res, $val, '<<', $places, 'preenche com zeros o lado direito');
$val = -4; $places = (PHP_INT_SIZE * 8) - 3; $res = $val << $places; p($res, $val, '<<', $places);
$val = -4; $places = (PHP_INT_SIZE * 8) - 2; $res = $val << $places; p($res, $val, '<<', $places, 'bits movidos para fora pela esquerda, incluindo o bit de sinal');
/* * Ignore essa seção abaixo, * é apenas para formatar a saída e deixar mais clara a apresentação. */
function p($res, $val, $op, $places, $note = '') { $format = '%0' . (PHP_INT_SIZE * 8) . "b\n";
printf("Expressão: %d = %d %s %d\n", $res, $val, $op, $places);
echo " Decimal:\n"; printf(" val=%d\n", $val); printf(" res=%d\n", $res);
echo " Binário:\n"; printf(' val=' . $format, $val); printf(' res=' . $format, $res);
if ($note) { echo " NOTA: $note\n"; }
echo "\n"; } ?>
&example.outputs.32bit;
