refentry.refsect1:
  &reftitle.examples;

   Um exemplo de apcu_entry


<?php
$config = apcu_entry("config", function($key) {
 return [
   "fruta" => apcu_entry("config.fruta", function($key){
     return [
       "abacaxis",
       "bananas"
     ];
   }),
   "pessoas" => apcu_entry("config.pessoas", function($key){
     return [
      "Marcelo",
      "Pedro",
      "Maria"
     ];
   })
 ];
});

var_dump($config);
?>


   &example.outputs;


array(2) {
  ["fruta"]=>
  array(2) {
    [0]=>
    string(6) "abacaxis"
    [1]=>
    string(5) "bananas"
  }
  ["pessoas"]=>
  array(3) {
    [0]=>
    string(3) "Marcelo"
    [1]=>
    string(3) "Pedro"
    [2]=>
    string(4) "Maria"
  }
}
