refentry.refsect1:
  &reftitle.examples;



    Exemplo da função array_filter


<?php
function impar($var)
{
    // retorna se o inteiro informado é impar
    return $var & 1;
}

function par($var)
{
    // retorna se o inteiro informado é par
    return !($var & 1);
}

$array1 = ['a' => 1, 'b' => 2, 'c' => 3, 'd' => 4, 'e' => 5];
$array2 = [6, 7, 8, 9, 10, 11, 12];

echo "Ímpares: \n";
print_r(array_filter($array1, "impar"));
echo "Pares: \n";
print_r(array_filter($array2, "par"));
?>


    &example.outputs;


Ímpares:
Array
(
    [a] => 1
    [c] => 3
    [e] => 5
)
Pares:
Array
(
    [0] => 6
    [2] => 8
    [4] => 10
    [6] => 12
)




    array_filter sem
    callback


<?php

$entry = [
    0 => 'foo',
    1 => false,
    2 => -1,
    3 => null,
    4 => '',
    5 => '0',
    6 => 0,
];

print_r(array_filter($entry));
?>


    &example.outputs;


Array
(
    [0] => foo
    [2] => -1
}




    array_filter com
    mode


<?php

$arr = ['a' => 1, 'b' => 2, 'c' => 3, 'd' => 4];

var_dump(array_filter($arr, function($k) {
    return $k == 'b';
}, ARRAY_FILTER_USE_KEY));

var_dump(array_filter($arr, function($v, $k) {
    return $k == 'b' || $v == 4;
}, ARRAY_FILTER_USE_BOTH));
?>


    &example.outputs;


array(1) {
  ["b"]=>
  int(2)
}
array(2) {
  ["b"]=>
  int(2)
  ["d"]=>
  int(4)
}

&reftitle.notes;



  Se o array é modificado por uma função callback (e.g. elemento
  adicionado, deletado ou apagado) o comportamento desta função é indefinido.
