refentry.refsect1:
  &reftitle.examples;

   Exemplo de bzread


<?php

$file = "/tmp/foo.bz2";
$bz = bzopen($file, "r") or die("Não foi possível abrir $file");

$decompressed_file = '';
while (!feof($bz)) {
    $decompressed_file .= bzread($bz, 4096);
}
bzclose($bz);

echo "O conteúdo de $file é: <br />\n";
echo $decompressed_file;

?>
