refentry.refsect1:
  &reftitle.examples;

   Exemplo de cubrid_pconnect_with_url e URL sem propriedades


<?php
$conn_url = "CUBRID:127.0.0.1:33000:demodb:dba::";
$con = cubrid_pconnect_with_url ($conn_url);

if ($con) {
    echo "conectado com sucesso";
    cubrid_execute($con, "create table person(id int,name char(16))");
    $req =cubrid_execute($con, "insert into person values(1,'James')");

    if ($req) {
        cubrid_close_request ($req);
        cubrid_commit ($con);
    } else {
        cubrid_rollback ($con);
    }
    cubrid_disconnect ($con);
}
?>





   Exemplo de cubrid_pconnect_with_url e URL com propriedades


<?php
$conn_url = "CUBRID:127.0.0.1:33000:demodb:dba::?althost=10.34.63.132:33088&rctime=100";
$con = cubrid_pconnect_with_url ($conn_url);

if ($con) {
    echo "conectado com sucesso";
    $req =cubrid_execute($con, "insert into person values(1,'James')");

    if ($req) {
        cubrid_close_request ($req);
        cubrid_commit ($con);
    } else {
        cubrid_rollback ($con);
    }
    cubrid_disconnect ($con);
}
?>
