refentry.refsect1:
&reftitle.description;

 mixedcurl_getinfo
 CurlHandlehandle
 intnulloption&null;



 Obtém informação sobre a última transferência.

  &reftitle.examples;



    Exemplo de curl_getinfo


<?php
// Cria um manipulador cURL
$ch = curl_init('http://www.example.com/');

// Executa
curl_exec($ch);

// Verifica se algum erro ocorreu
if (!curl_errno($ch)) {
  $info = curl_getinfo($ch);
  echo 'Demorou ', $info['total_time'], ' segundos para enviar uma requisição a ', $info['url'], "\n";
}

// Fecha o manipulador
curl_close($ch);
?>







    Exemplo de curl_getinfo com parâmetro option


<?php
// Cria um manipulador cURL
$ch = curl_init('http://www.example.com/');

// Executa
curl_exec($ch);

// Verifica o código de estado do HTTP
if (!curl_errno($ch)) {
  switch ($http_code = curl_getinfo($ch, CURLINFO_HTTP_CODE)) {
    case 200:  # OK
      break;
    default:
      echo 'Código HTTP inesperado: ', $http_code, "\n";
  }
}

// Fecha o manipulador
curl_close($ch);
?>
