  &reftitle.examples;




	 Modo conveniente de como glob pode susbstituir
	 opendir e companhia.



<?php
foreach (glob("*.txt") as $arquivo) {
    echo "tamanho de $arquivo " . filesize($arquivo) . "\n";
}
?>


