refentry.refsect1:
  &reftitle.examples;



    Exemplo de oci_parse para instruções SQL


<?php

$conn = oci_connect('hr', 'welcome', 'localhost/XE');

// Analisa a instrução. Observe que não há ponto e vírgula final na instrução SQL
$stid = oci_parse($conn, 'SELECT * FROM employees');
oci_execute($stid);

echo "<table border='1'>\n";
while ($row = oci_fetch_array($stid, OCI_ASSOC+OCI_RETURN_NULLS)) {
    echo "<tr>\n";
    foreach ($row as $item) {
        echo "    <td>" . ($item !== null ? htmlentities($item, ENT_QUOTES) : "&nbsp;") . "</td>\n";
    }
    echo "</tr>\n";
}
echo "</table>\n";

?>







    Exemplo de oci_parse para instruções PL/SQL


<?php

/*
  Antes de executar o programa PHP, crie um procedimento armazenado no
  SQL*Plus ou SQL Developer:

  CREATE OR REPLACE PROCEDURE myproc(p1 IN NUMBER, p2 OUT NUMBER) AS
  BEGIN
      p2 := p1 * 2;
  END;

*/

$conn = oci_connect('hr', 'welcome', 'localhost/XE');
if (!$conn) {
    $e = oci_error();
    trigger_error(htmlentities($e['message'], ENT_QUOTES), E_USER_ERROR);
}

$p1 = 8;

// Ao analisar programas PL/SQL, deve haver um ponto e vírgula final na string
$stid = oci_parse($conn, 'begin myproc(:p1, :p2); end;');
oci_bind_by_name($stid, ':p1', $p1);
oci_bind_by_name($stid, ':p2', $p2, 40);

oci_execute($stid);

print "$p2\n";   // exibe 16

oci_free_statement($stid);
oci_close($conn);

?>
