refentry.refsect1:
&reftitle.description;

 stringsodium_crypto_secretbox_keygen



 Gerar uma chave para uso com sodium_crypto_secretbox e sodium_crypto_secretbox_open.

  &reftitle.examples;



    Exemplo de sodium_crypto_secretbox_keygen


<?php
$key = sodium_crypto_secretbox_keygen();
var_dump( sodium_bin2hex( $key ) );
?>


    &example.outputs.similar;


string(64) "88bd1dc51ec81984f3ddc5a8f59a3d95b647e2da3e879f1b9ceb0abd89e7286c"






     Comparando sodium_crypto_secretbox_keygen
     com random_bytes



<?php
$key = sodium_crypto_secretbox_keygen();
$bytes = random_bytes( SODIUM_CRYPTO_SECRETBOX_KEYBYTES );
var_dump( mb_strlen( $key, '8bit' ) === mb_strlen( $bytes, '8bit' ) );
?>


    &example.outputs;


bool(true)
