refentry.refsect1:
&reftitle.parameters;

 &stomp.param.link;

  msg



    A mensagem ou o ID de mensagem a ser reconhecido.



 &stomp.param.headers;

  &reftitle.examples;

   &style.oop;


<?php

$queue  = '/queue/foo';
$msg    = 'bar';

/* conexão */
try {
    $stomp = new Stomp('tcp://localhost:61613');
} catch(StompException $e) {
    die('Conexão falhou: ' . $e->getMessage());
}

/* envia uma mensagem à fila 'foo' */
$stomp->send($queue, $msg);

/* inscreve para mensagens da fila 'foo' */
$stomp->subscribe($queue);

/* lê um quadro */
$frame = $stomp->readFrame();

if ($frame->body === $msg) {
    /* confirma que o quadro foi recebido */
    $stomp->ack($frame);
}

/* remove a inscrição */
$stomp->unsubscribe($queue);

/* fecha a conexão */
unset($stomp);

?>




   &style.procedural;


<?php

$queue  = '/queue/foo';
$msg    = 'bar';

/* conexão */
$link = stomp_connect('ssl://localhost:61612');

/* verifica a conexão */
if (!$link) {
    die('Conexão falhou: ' . stomp_connect_error());
}

/* começa uma transação */
stomp_begin($link, 't1');

/* envia uma mensagem à fila 'foo' */
stomp_send($link, $queue, $msg, array('transaction' => 't1'));

/* confirma uma transação */
stomp_commit($link, 't1');

/* inscreve para mensagens da fila 'foo' */
stomp_subscribe($link, $queue);

/* lê um quadro */
$frame = stomp_read_frame($link);

if ($frame['body'] === $msg) {
    /* confirma que o quadro foi recebido */
    stomp_ack($link, $frame['headers']['message-id']);
}

/* remove a inscrição */
stomp_unsubscribe($link, $queue);

/* fecha a conexão */
stomp_close($link);

?>
