<?php

/* conexão */
$link = stomp_connect('ssl://localhost:61612');

/* verifica a conexão */
if (!$link) {
    die('Conexão falhou: ' . stomp_connect_error());
}

var_dump(stomp_get_session_id($link));

/* fecha a conexão */
stomp_close($link);

?>


    &example.outputs.similar;


